CREATE TABLE IF NOT EXISTS location_centres_agencies ( agency_id INT NOT NULL AUTO_INCREMENT, name VARCHAR(255) NOT NULL, created_on DATETIME NULL DEFAULT CURRENT_TIMESTAMP, created_by INT(11) NOT NULL DEFAULT 1,
PRIMARY KEY (agency_id), UNIQUE INDEX name_agency_unique (name ASC), CONSTRAINT fk_location_agency_person1 FOREIGN KEY (created_by) REFERENCES person(person_id) ON DELETE NO ACTION ON UPDATE NO ACTION );
CREATE TABLE IF NOT EXISTS location_centres_regions ( region_id INT NOT NULL AUTO_INCREMENT, name VARCHAR(255) NOT NULL, created_on DATETIME NULL DEFAULT CURRENT_TIMESTAMP, created_by INT(11) NOT NULL DEFAULT 1,
PRIMARY KEY (region_id), UNIQUE INDEX name_region_unique (name ASC), CONSTRAINT fk_location_region_person1 FOREIGN KEY (created_by) REFERENCES person(person_id) ON DELETE NO ACTION ON UPDATE NO ACTION );
CREATE TABLE IF NOT EXISTS location_centres ( centre_id INT NOT NULL AUTO_INCREMENT, serial_number VARCHAR(255) NOT NULL, location_id INT(11) NOT NULL, region_id INT(11) NOT NULL, agency_id INT(11) NOT NULL, created_on DATETIME NULL DEFAULT CURRENT_TIMESTAMP, created_by INT(11) NOT NULL DEFAULT 1,
PRIMARY KEY (centre_id), UNIQUE INDEX name_centre_unique (serial_number ASC), CONSTRAINT fk_location_centres_location FOREIGN KEY (location_id) REFERENCES Location (location_id) ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT fk_location_centres_region FOREIGN KEY (region_id) REFERENCES location_centres_regions (region_id) ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT fk_location_centres_agency FOREIGN KEY (agency_id) REFERENCES location_centres_agencies (agency_id) ON DELETE NO ACTION ON UPDATE NO ACTION, CONSTRAINT fk_location_centres_person1 FOREIGN KEY (created_by) REFERENCES person (person_id) ON DELETE NO ACTION ON UPDATE NO ACTION );
